package cn.lliyuu520.haozi.common.cache;import cn.lliyuu520.haozi.modules.sys.vo.SysAreaNode;import jakarta.annotation.Resource;import lombok.RequiredArgsConstructor;import org.springframework.data.redis.core.RedisTemplate;import org.springframework.stereotype.Component;import java.util.List;/** * 行政区划缓存 * * @author liliangyu */@Component@RequiredArgsConstructorpublic class SysAreaCache {    @Resource    private RedisTemplate<String, List<SysAreaNode>> redisTemplate;    private final static String KEY = "sys:area";    /**     * 设置     * @param sysAreaNodeList     */    public void set(List<SysAreaNode> sysAreaNodeList) {        redisTemplate.opsForValue().set(KEY, sysAreaNodeList);    }    /**     * 获取     * @return     */    public List<SysAreaNode> get() {        return redisTemplate.opsForValue().get(KEY);    }}