package cn.lliyuu520.haozi.common.config;/** * 图片压缩参数 * * @author liliangyu */import lombok.Data;import java.io.Serializable;/** * 图片压缩配置 */@Datapublic class ImageCompressionConfig implements Serializable {    /**     * 压缩后最大宽度     */    private int maxWidth = 200;    /**     * 压缩后最大高度     */    private int maxHeight = 150;    /**     * JPEG压缩质量 (0.0-1.0)     */    private float jpegQuality = 0.75f;    /**     * 目标文件大小上限(KB)     */    private int maxFileSizeKb = 50;    /**     * 网络请求超时时间(毫秒)     */    private int timeoutMs = 5000;    /**     * 最大重试次数     */    private int maxRetries = 2;    /**     * 线程池大小     */    private int threadPoolSize = 10;}