package cn.lliyuu520.haozi.common.enums;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.ToString;/** * 报单状态(未审核,通过,拒绝) * * @author liliangyu */@AllArgsConstructor@ToString@Getterpublic enum AuditStatusEnums {    /**     * 未审核     */    UNAUDITED("UNAUDITED", "未审核"),    /**     * 通过     */    PASS("PASS", "通过"),    /**     * 拒绝     */    REJECT("REJECT", "拒绝"),    ;    /**     * 编码     */    private final String code;    /**     * 描述     */    private final String desc;    public static String getDescByCode(String code) {        for (AuditStatusEnums value : AuditStatusEnums.values()) {            if (value.getCode().equals(code)) {                return value.getDesc();            }        }        return null;    }}