package cn.lliyuu520.haozi.common.init;import cn.lliyuu520.haozi.modules.sys.service.SysConfigService;import lombok.RequiredArgsConstructor;import org.springframework.boot.CommandLineRunner;import org.springframework.stereotype.Component;/** * 系统参数初始化 * * @author zhuzhi * @date 2021/5/12 */@Component@RequiredArgsConstructorpublic class SysConfigInit  implements CommandLineRunner {    private final SysConfigService sysConfigService;    /**     * Callback used to run the bean.     *     * @param args incoming main method arguments     * @throws Exception on error     */    @Override    public void run(String... args) throws Exception {        sysConfigService.init();    }}