package cn.lliyuu520.haozi.modules.sys.dto;import cn.lliyuu520.haozi.modules.sys.enums.SseNotifyType;import lombok.Data;import java.io.Serializable;/** * * * @author liliangyu */@Datapublic class SseNotifyDTO  implements Serializable {    /**     * 类型     */    private String type;    /**     *  标题     */    private String title;    /**     * 消息     */    private String message;    /**     * 链接     */    private String url;    private String fileName;    public static SseNotifyDTO build(SseNotifyType sseNotifyType) {        final SseNotifyDTO sseNotifyDTO = new SseNotifyDTO();        sseNotifyDTO.setType( sseNotifyType.getType());        sseNotifyDTO.setTitle( sseNotifyType.getTitle());       return sseNotifyDTO;    }}